import React, { useState, useContext } from 'react';
import API from '../context/Api.js';

function Login() {
  const { postLogin } = API; // Importa la función postLogin desde la API.

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      // Llama a la función postLogin con las credenciales del usuario.
      const formData = {
        username,
        password,
      };
      const response = await postLogin(formData);

      if (response.success) {
        // Si la autenticación tiene éxito, puedes redirigir al usuario o realizar otras acciones.
        console.log('Inicio de sesión exitoso');
      } else {
        setError('Error al iniciar sesión. Comprueba tus credenciales.');
      }
    } catch (err) {
      setError('Error al iniciar sesión. Comprueba tu conexión de red.');
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <p className="error">{error}</p>}
      <form>
        <div>
          <label htmlFor="username">Nombre de usuario:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Contraseña:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="button" onClick={handleLogin}>
          Iniciar Sesión
        </button>
      </form>
    </div>
  );
}

export default Login;
