import React, { useState } from 'react';
import { Route, Routes, BrowserRouter } from 'react-router-dom';
import Home from './pages/Home';
import Form from './pages/Form';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import List from './pages/List';

function App() {
  const [token, setToken] = useState(null); // Estado para almacenar el token de inicio de sesión

  // Función para actualizar el token
  const updateToken = (newToken) => {
    setToken(newToken);
  };

  // Componente de navegación personalizado
  const SecuredRoute = ({ path, element }) => {
    // Verifica si hay un token y lo adjunta a la solicitud al backend si es necesario
    const fetchOptions = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    return <Route path={path} element={React.cloneElement(element, { fetchOptions })} />;
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login updateToken={updateToken} />} />
        <SecuredRoute path="/form" element={<Form />} />
        <SecuredRoute path="/dashboard" element={<Dashboard />} />
        <SecuredRoute path="/listform" element={<List />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
