import React, { useState, useEffect } from 'react';

function List() {
  // Define un estado para almacenar la lista de formularios
  const [formList, setFormList] = useState([]);

  // Simula una llamada a la API para obtener los datos de los formularios
  useEffect(() => {
    // Reemplaza esta parte con una llamada real a la API
    const fetchData = async () => {
      try {
        // Supongamos que obtienes los datos de la API asíncronamente
        const response = await fetch('http://localhost:8000/form');
        const data = await response.json();

        // Actualiza el estado con los datos de los formularios
        setFormList(data);
      } catch (error) {
        console.error('Error al obtener los datos de la API:', error);
      }
    };

    // Llama a la función fetchData para obtener los datos
    fetchData();
  }, []); // El segundo argumento [] asegura que esto solo se ejecute una vez

  return (
    <div>
      <h1>Lista de Formularios</h1>
      <ul>
        {formList.map((form) => (
          <li key={form._id}>
            <strong>Nombre: </strong> {form.name}<br />
            <strong>Email: </strong> {form.email}<br />
            <strong>Asunto: </strong> {form.subject}<br />
            <strong>Mensaje: </strong> {form.message}<br />
            <strong>Estado: </strong> {form.status}<br />
            <strong>Creado por: </strong> {form.createdBy}<br />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default List;
