import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';

const Main = () => {
  return (
    <div className="main-container bg-light min-vh-100 d-flex flex-column justify-content-between">
      <Container fluid>
        <header className="text-center my-4">
          <h1 className="display-4">Bienvenido a CyberGuard</h1>
          <p className="lead">Tu socio en ciberseguridad</p>
        </header>

        <section className="services text-center my-4">
          <h2>Nuestros Servicios</h2>
          <Row>
            {[
              {
                title: 'Seguridad de Datos',
                description: 'Protegemos tus datos más valiosos contra amenazas cibernéticas, empleando las últimas tecnologías y estrategias de seguridad.',
                color: 'primary',
              },
              {
                title: 'Monitoreo Continuo',
                description: 'Nuestro equipo de expertos vigila constantemente tu red para detectar y prevenir actividades sospechosas, garantizando la integridad de tu sistema.',
                color: 'success',
              },
              {
                title: 'Evaluación de Vulnerabilidades',
                description: 'Identificamos y solucionamos debilidades en tu infraestructura de TI para que puedas estar un paso adelante de posibles amenazas.',
                color: 'warning',
              },
            ].map((service, index) => (
              <Col md={4} className={`service text-${service.color}`} key={index}>
                <div className="p-4 border rounded">
                  <h3>{service.title}</h3>
                  <p>{service.description}</p>
                </div>
              </Col>
            ))}
          </Row>
        </section>
      </Container>

      <section className="cta text-center my-2">
        <h2>¿Listo para fortalecer tu seguridad en línea?</h2>
        <p>
          Únete a nosotros y protege tu empresa en el mundo digital. Contáctanos para obtener una consulta gratuita.
        </p>
        <a href="/form" className="nav-link">
          <Button className="cta-button btn-danger">Contáctanos</Button>
        </a>
      </section>
    </div>
  );
};

export default Main;
