import React, { useState, useEffect } from 'react';
import { Route, Routes, BrowserRouter, Navigate } from 'react-router-dom';
import Home from './pages/Home';
import Form from './pages/Form';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import List from './pages/List';

function App() {
  const [token, setToken] = useState(null); // Estado para almacenar el token

  // Verificar si el usuario ha iniciado sesión
  useEffect(() => {
    const userToken = localStorage.getItem('token'); // Puedes ajustar esto según tu lógica de inicio de sesión
    if (userToken) {
      setToken(userToken);
    }
  }, []);

  // Función para manejar el inicio de sesión
  const handleLogin = (userToken) => {
    setToken(userToken);
    localStorage.setItem('token', userToken); // Almacena el token en localStorage
  };

  // Función para manejar el cierre de sesión
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token'); // Elimina el token de localStorage
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/form" element={token ? <Form /> : <Navigate to="/login" />} />
        <Route path="/dashboard" element={token ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/listform" element={token ? <List /> : <Navigate to="/login" />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
