import React, { useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Form, Button, Alert } from "react-bootstrap";
import { Redirect } from "react-router-dom"; // Importa Redirect desde react-router-dom
import Header from "../components/header/Header";
import Footer from "../components/footer/Footer";

function Login() {
  // Estado para guardar los datos del formulario
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  // Estado para manejar errores
  const [error, setError] = useState(null);

  // Estado para controlar la carga
  const [loading, setLoading] = useState(false);

  // Estado para redireccionar a diferentes páginas
  const [redirect, setRedirect] = useState(false);

  // Función para manejar cambios en los campos del formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const response = await axios.post(
        "http://localhost:8000/user/login",
        formData
      );

      if (response.data.token) {
        localStorage.setItem("token", response.data.token);
        console.log("Token de acceso guardado en localStorage.");

        // Suponiendo que el token contiene información sobre el tipo de usuario
        const userType = response.data.userType;

        if (userType === "admin") {
          // Redirecciona a la página de administrador
          setRedirect("/admin");
        } else if (userType === "user") {
          // Redirecciona a la página de usuario normal
          setRedirect("/user");
        }
      } else {
        setError("No se recibió un token de acceso.");
      }
    } catch (error) {
      setError("Error al iniciar sesión. Verifica tus credenciales.");
      console.error("Error al iniciar sesión:", error);
    } finally {
      setLoading(false);
    }
  };

  // Si redirect es verdadero, redirige a la página correspondiente
  if (redirect) {
    return <Redirect to={redirect} />;
  }

  return (
    <>
      <Header />
      <Container className="mt-5">
        <Row className="justify-content-center" style={{ minHeight: "70vh" }}>
          <Col md={4} style={{ marginTop: "140px" }}>
            <Card bg="dark" text="white">
              <Card.Body>
                <h2 className="card-title text-center mb-4">Iniciar sesión</h2>
                {/* Muestra el mensaje de error si existe */}
                {error && <Alert variant="danger">{error}</Alert>}
                <Form onSubmit={handleSubmit}>
                  <Form.Group>
                    <Form.Label>Nombre de usuario:</Form.Label>
                    {/* Campo de entrada para el nombre de usuario */}
                    <Form.Control
                      type="text"
                      name="username"
                      value={formData.username}
                      onChange={handleInputChange}
                      required
                      placeholder="Ingresa tu nombre de usuario"
                    />
                  </Form.Group>
                  <Form.Group style={{ marginTop: "20px" }}>
                    <Form.Label>Contraseña:</Form.Label>
                    {/* Campo de entrada para la contraseña */}
                    <Form.Control
                      type="password"
                      name="password"
                      value={formData.password}
                      onChange={handleInputChange}
                      required
                      placeholder="Ingresa tu contraseña"
                    />
                  </Form.Group>
                  {/* Botón para enviar el formulario */}
                  <Button
                    type="submit"
                    variant="primary"
                    className="btn-dark text-white mx-auto"
                    disabled={loading}
                    style={{
                      marginTop: "20px",
                      marginLeft: "120px",
                      fontSize: "1.2rem",
                      transition: "background-color 0.3s ease, color 0.3s ease",
                      cursor: "pointer",
                      borderRadius: "4px",
                      width: "150px",
                    }}
                  >
                    {/* Cambia el texto del botón según el estado de carga */}
                    {loading ? "Iniciando sesión..." : "Iniciar sesión"}
                  </Button>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
      <Footer />
    </>
  );
}

export default Login;
