import React, { useState } from 'react';
import * as Yup from 'yup';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';

function Login() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const [errors, setErrors] = useState({});
  const [loggedIn, setLoggedIn] = useState(false); // Variable para rastrear el inicio de sesión exitoso

  const schema = Yup.object().shape({
    username: Yup.string().required('Username es obligatorio'),
    password: Yup.string()
      .required('Password es obligatorio')
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
        'Password debe cumplir los requisitos de seguridad.'
      ),
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await schema.validate(formData, { abortEarly: false });

      const response = await fetch('http://localhost:8000/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Lógica de inicio de sesión exitosa
        const responseData = await response.json();
        // Almacena el token en el Local Storage
        localStorage.setItem('token', responseData.token);
        setLoggedIn(true);
      } else {
        const responseData = await response.json();
        setErrors(responseData.errors);
      }
    } catch (validationErrors) {
      const errors = {};
      validationErrors.inner.forEach((error) => {
        errors[error.path] = error.message;
      });
      setErrors(errors);
    }
  };

  // Redirige al usuario a la página "list" después del inicio de sesión exitoso
  if (loggedIn) {
    window.location.href = '/list';
    return null;
  }

  return (
    <>
      <Header />
      <div className="container mt-5 d-flex justify-content-center align-items-center" style={{ minHeight: '70vh' }}>
        <div className="card w-100" style={{ maxWidth: '400px' }}>
          <div className="card-header bg-dark text-white text-center">
            <h4 className="mb-0">Login</h4>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Username
                </label>
                <input
                  type="text"
                  className={`form-control ${errors.username ? 'is-invalid' : ''}`}
                  id="username"
                  placeholder="Enter username"
                  required
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                />
                {errors.username && <div className="invalid-feedback">{errors.username}</div>}
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">
                  Password
                </label>
                <input
                  type="password"
                  className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                  id="password"
                  placeholder="Password"
                  required
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                />
                {errors.password && <div className="invalid-feedback">{errors.password}</div>}
              </div>
              <div className="d-grid">
                <button type="submit" className="btn btn-dark">
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default Login;
